def solve_problem(naomi, yuri, brianna, jojo):
    if yuri > 2 * naomi:
        return 2 * naomi + brianna + jojo
    elif yuri == 2 * naomi:
        return naomi + 2 * brianna + jojo
    else:
        return yuri + naomi + 2 * brianna + jojo
